TABLEATTRIBUTE.VALUE:
/* ************************************************************************ */
/* S_INQUIRY_CLAIMS_DATA */
/* Abstract: Creates the S_INQUIRY_CLAIMS_DATA data as part of the */
/* process to create the R_POLICY_CLAIM_LOOKUP_10YR. This is the */
/* union of the S_REG_AND_PRO_CLAIMS_DAILY table and the */
/* S_FDW_ONLY_CLAIMS table. */
/* */
/* Revision Date: Jan 15, 2014 */
/* Revised By: Chris Fitz, Cognos Business Intelligence Developer */
/* Revision Summary: Added the REPORTED_DT field as requested in QC5260. */
/* */
/* ************************************************************************ */
SELECT a.policy_symbol ,a.policy_number ,a.policy_module ,a.master_company ,a.company_id ,a.claim_number ,a.loss_date ,a.status_cd ,NVL(a.claimant_name, a.insured_name) claimant_name ,a.claimant_ssn ,a.occurrence_desc ,a.field_claim_office ,a.field_claim_office_name ,a.claim_adjuster_id ,DECODE( a.claim_adjuster_whole_name ,'NAME UNKNOWN', a.claim_adjuster_whole_name ,RTRIM(a.claim_adjuster_first_name) || ' ' || RTRIM(a.claim_adjuster_last_name) ) claim_adjuster_name ,a.recovery_adjuster_id ,DECODE( a.recovery_adjuster_whole_name ,'NAME UNKNOWN', a.recovery_adjuster_whole_name ,RTRIM(a.recovery_adjuster_first_name) || ' ' || RTRIM(a.recovery_adjuster_last_name) ) recovery_adjuster_name ,a.insured_name ,a.closed_date ,(CASE WHEN (SUBSTR( a.claim_number, 1, 3) = '491') THEN 'I-360' ELSE 'PRO' END) source_system ,a.reported_dt -- New Field added as per QC 5260 ,TO_DATE( LTRIM(a.policy_effective_dt), 'yymmdd') FROM stage.s_reg_and_pro_claims_daily a WHERE (((LTRIM(a.status_cd) IS NULL) OR (a.status_cd <> '3')) AND ((a.status_cd = '1') OR (a.closed_date >= TO_CHAR( SYSDATE - 10 * 365, 'YYYYMMDD')) OR ((LTRIM(a.status_cd) IS NULL) AND (LTRIM(a.closed_date) IS NULL) AND (a.loss_date >= TO_CHAR( SYSDATE - 10 * 365, 'YYYYMMDD'))))) OR ((a.status_cd = '3') AND (RTRIM(a.source_system) IN ('Claims Registration - IO', 'Claims Registration Only')) AND (a.closed_date >= TO_CHAR( SYSDATE - 10 * 365, 'YYYYMMDD'))) UNION SELECT a.policy_symbol ,a.policy_number ,a.policy_module ,a.master_company ,a.company_id ,a.claim_number ,a.loss_date ,a.status_cd ,a.claimant_name ,a.claimant_ssn ,a.occurrence_desc ,a.field_claim_office ,a.field_claim_office_name ,a.claim_adjuster_id ,DECODE( a.claim_adjuster_whole_name ,'NAME UNKNOWN', a.claim_adjuster_whole_name ,RTRIM(a.claim_adjuster_first_name) || ' ' || RTRIM(a.claim_adjuster_last_name) ) claim_adjuster_name ,a.recovery_adjuster_id ,DECODE( a.recovery_adjuster_whole_name ,'NAME UNKNOWN', a.recovery_adjuster_whole_name ,RTRIM(a.recovery_adjuster_first_name) || ' ' || RTRIM(a.recovery_adjuster_last_name) ) recovery_adjuster_name ,a.insured_name ,a.closed_date ,a.source_system ,a.reported_dt -- New Field added as per QC 5260 ,a.policy_effective_date FROM stage.s_fdw_only_claims a WHERE a.status_cd = '1' OR (a.closed_date >= TO_CHAR( SYSDATE - 10 * 365, 'YYYYMMDD')) OR ((LTRIM(a.status_cd) IS NULL) AND (LTRIM(a.closed_date) IS NULL) AND (a.loss_date >= TO_CHAR( SYSDATE - 10 * 365, 'YYYYMMDD'))) OR a.source_system = 'FISRM'
